(9, 1, '2022-07-17 03:38:45', '2022-07-17 03:38:45', '<!-- wp:group {\"tagName\":\"section\",\"className\":\"site_hero\",\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\",\"justifyContent\":\"center\",\"orientation\":\"horizontal\"}} -->\n<section class=\"wp-block-group site_hero\"><!-- wp:columns {\"className\":\"ctr\"} -->\n<div class=\"wp-block-columns ctr\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:heading {\"level\":1} -->\n<h1>Weâre here to Increase your Productivity</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Let\'s make your work more organize and easily using the Taskio Dashboard with many of the latest featuresin managing work every day.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\">Try free trial</a></div>\n<!-- /wp:button -->\n\n<!-- wp:button {\"className\":\"is-style-outline play-view-btn\"} -->\n<div class=\"wp-block-button is-style-outline play-view-btn\"><a class=\"wp-block-button__link\">View Demo</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:image {\"align\":\"center\",\"id\":56,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"site_hero-img\"} -->\n<figure class=\"wp-block-image aligncenter size-full site_hero-img\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/hero_art.png\" alt=\"\" class=\"wp-image-56\" title=\"hero art\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:html -->\n<div class=\"blurred_circle\"></div>\n<!-- /wp:html --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></section>\n<!-- /wp:group -->\n\n<!-- wp:group {\"tagName\":\"section\",\"className\":\"ctr page-section\"} -->\n<section class=\"wp-block-group ctr page-section\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">More than 25,000 teams use Collabs</h2>\n<!-- /wp:heading -->\n\n<!-- wp:shortcode -->\n[team_collabs]\n<!-- /wp:shortcode --></section>\n<!-- /wp:group -->\n\n<!-- wp:group {\"tagName\":\"section\",\"className\":\"page-compact-section gray-bg-color\"} -->\n<section class=\"wp-block-group page-compact-section gray-bg-color\"><!-- wp:group {\"tagName\":\"section\",\"className\":\"ctr\"} -->\n<section class=\"wp-block-group ctr\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading -->\n<h2>How we support our pratner all over the world</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>SaaS become a common delivery model for many business application, including office software, messaging software, payroll processing software, DBMS software, management software</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:shortcode -->\n[support_ratings]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"support_types\"} -->\n<div class=\"wp-block-column support_types\"><!-- wp:media-text {\"mediaId\":73,\"mediaLink\":\"http://localhost/biccas/home/activity-1/\",\"mediaType\":\"image\",\"mediaWidth\":15} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:15% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/activity-1.svg\" alt=\"\" class=\"wp-image-73 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Publishing</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>and publishing your contetn that drivees meaningful engagement and growth for your barnd</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaId\":81,\"mediaLink\":\"http://localhost/biccas/home/pie-chart-1/\",\"mediaType\":\"image\",\"mediaWidth\":15} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:15% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/pie-chart-1.svg\" alt=\"\" class=\"wp-image-81 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Analytics</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Analyze your performance and create goegeous report</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaId\":82,\"mediaLink\":\"http://localhost/biccas/home/command-1/\",\"mediaType\":\"image\",\"mediaWidth\":15} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:15% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/command-1.svg\" alt=\"\" class=\"wp-image-82 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:heading {\"level\":3} -->\n<h3>Engagement</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Quiuckly navigate you anda engage with your adience</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></section>\n<!-- /wp:group --></section>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"page-section ctr our_feats\"} -->\n<div class=\"wp-block-group page-section ctr our_feats\"><!-- wp:columns {\"className\":\"row_one\"} -->\n<div class=\"wp-block-columns row_one\"><!-- wp:column {\"width\":\"33.33%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.33%\"><!-- wp:heading -->\n<h2>Our Features you cab get</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"66.66%\",\"className\":\"text_cta\"} -->\n<div class=\"wp-block-column text_cta\" style=\"flex-basis:66.66%\"><!-- wp:paragraph -->\n<p>We offer a variety of interesting features that you can help increase your productivity at work and manage your project easily</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:block {\"ref\":105} /--></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":\"74px\"} -->\n<div style=\"height:74px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns {\"className\":\"feats_card\"} -->\n<div class=\"wp-block-columns feats_card\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":120,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/card_one.svg\" alt=\"\" class=\"wp-image-120\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Collaboration Teams</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Here you can handle projects together with team virtually</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":119,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/card_two.svg\" alt=\"\" class=\"wp-image-119\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Cloud Storage</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>No need to worry about storage because we provide storage up to 2 TB</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":118,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/card_three.svg\" alt=\"\" class=\"wp-image-118\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Daily Analytics</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>We always provide useful information to make it easier for you every day</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"className\":\"ctr\"} -->\n<div class=\"wp-block-columns ctr\"><!-- wp:column {\"className\":\"benefit_items\"} -->\n<div class=\"wp-block-column benefit_items\"><!-- wp:heading -->\n<h2>What Benifit Will You Get</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list {\"className\":\"checklist\"} -->\n<ul class=\"checklist\"><li>Free Consulting With Expert Saving Money</li><li>Online Banking</li><li>Investment Report Every Month</li><li>Saving Money For The Future</li><li>Online Transection</li></ul>\n<!-- /wp:list --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":123,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/biccas/wp-content/uploads/2022/07/What-Benifit-Will-You-Get.png\" alt=\"\" class=\"wp-image-123\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:group {\"className\":\"page-section ctr plan_selector_outer\"} -->\n<div class=\"wp-block-group page-section ctr plan_selector_outer\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Choose Plan<br>Thatâs Right For You</h2>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<div class=\"blurred_circle\"></div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Choose plan that works best for you, feel free to contact us</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:shortcode -->\n[pricing_table]\n<!-- /wp:shortcode --></div>\n<!-- /wp:group -->\n\n<!-- wp:block {\"ref\":225} /-->', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2022-07-21 06:40:41', '2022-07-21 06:40:41', '', 0, 'http://localhost/biccas/?page_id=9', 0, 'page', '', 0),
